import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { listDecks, listCards } from "../../utils/api/index";
import Deck from "./Deck";
// const allData = require("../../data/db.json");
// import ErrorMessage from "../common/ErrorMessage";

export const DeckList = () => {
  const history = useHistory();
  const [decks, setDecks] = useState([]);
  const [error, setError] = useState(undefined);
  useEffect(() => {
    const abortController = new AbortController();
    listDecks(abortController.signal).then(setDecks).catch(setError);
    // console.log("ATTEMPTED UTILS RETRIEVAL OF DECKS, ", decks);

    // const abortController2 = new AbortController();
    // listCards(1, abortController2.signal).then(setCards).catch(setError);
    // console.log("ATTEMPTED UTILS RETRIEVAL OF 1st deck's cards, ", cards);
    return () => abortController.abort();
  }, []); // do only once

  if (error) {
    return "ERROR " + error; // <ErrorMessage error={error} />;
  }

  const list = decks.map((deckObj) =>
    <Deck key={deckObj['id']} deckObj={deckObj} />);

  return (
    <main className="container">
      <button onClick={() => {history.push("/decks/new")}} >Create Deck</button>
      <section className="row">{list}</section>
    </main>
  );
};

export default DeckList;
