import React, { useEffect, useState } from "react";
import { Link, NavLink } from "react-router-dom";
import { listCards } from "../../utils/api/index";

// TENTATIVELY DONE
/*
  TODO: Change the link below to go to the user route, using the user's ID.
  /users/:userId
*/
export const DeckView = ({ deckId /* = { posts: [] } */}) => {
  const [deckCards, setDeckCards] = useState([]);
  const [error, setError] = useState(undefined);
  useEffect(() => {
    const abortController = new AbortController();
    listCards(deckId,
      abortController.signal).then(setDeckCards).catch(setError);
  }, []);
  if (error) {
    return "ERROR " + error; // <ErrorMessage error={error} />;
  }

  const BASE_URL = `/users/${deckObj['id']}`;
  return (
    <article className="col-12 col-md-6 col-xl-3 my-2 align-self-stretch">
      <div className="border p-4 h-100 d-flex flex-column">
        <h2 className="font-weight-lighter flex-fill">
          {deckObj['name']}
        </h2>
        <p>
          {deckObj['description']}
        </p>
        <nav>
          <ul>
            <li>
              <button>Edit</button>
            </li>
            <li>
              <button>Study</button>
            </li>
            <li>
              <button>Add Cards</button>
            </li>
            <li>
              <button>Delete</button>
            </li>
          </ul>
        </nav>
      </div>
    </article>
)};

export default DeckView;
