import React, { useEffect, useState } from "react";
import { useHistory, useParams, useRouteMatch, Link, NavLink } from "react-router-dom";
import { readDeck } from "../../utils/api/index";

export const DeckEdit = ({deckId}) => {
    const params = useParams();
    // console.log("PARAMS IN EDIT, ", params);
    // const deckId = params['deckId'];
    console.log("DECK ID IN EDIT is ", deckId);

    const [thisDeck, setThisDeck] = useState({});
    const [error, setError] = useState(undefined);

    const [name, setName] = useState("");
    const [desc, setDesc] = useState("");
    const handleNameChange = (event) => setName(event['target']['value']);
    const handleDescChange = (event) => setDesc(event['target']['value']);
    useEffect(() => {
        const abortController = new AbortController();
        readDeck(deckId,
            abortController.signal).then(setThisDeck).catch(setError);
    }, []);

    if (error) {
        return "ERROR " + error; // <ErrorMessage error={error} />;
    }
    useEffect(() => {
        setName(thisDeck['name']);
        setDesc(thisDeck['description']);
    }, []);

    console.log("EDIT: THIS DECK", thisDeck);
    console.log("EDIT: NAME", name);
    console.log("EDIT: DESCRIPTION", desc);
    // setAge(parseInt(event['target']['value']));
    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(name, desc /*.toString()*/);
        // setName(() => ""); setDesc(() => ""); setAge(() => "");
    };
    
    return(
        <form onSubmit={handleSubmit}>
            <label htmlFor="name">
                Enter name:
                <input
                    id="name"
                    type="text"
                    name="name"
                    onChange={handleNameChange}
                    value={name}
                    defaultValue=""
                    // defaultValue={thisDeck['name']}
                />
            </label>
            <label htmlFor="desc">
                Description:
                <textarea
                    id="desc"
                    type="text"
                    name="desc"
                    onChange={handleDescChange}
                    value={desc}
                    defaultValue=""
                    // defaultValue={thisDeck['description']}
                />
            </label>
            <button type="submit">Submit</button>
      </form>
    );
}

export default DeckEdit;
