import React, { useEffect, useState } from "react";
import { useHistory, useParams, useRouteMatch, Switch, Route, Link, NavLink } from "react-router-dom";
import { readDeck, listCards } from "../../utils/api/index";
import DeckCard from "./DeckCard";
import DeckEdit from "./DeckEdit";

export const DeckView = (/* { deckId = { posts: [] }} */) => {
    const history = useHistory();
    const { url } = useRouteMatch();
    const params = useParams();
    const deckId = params['deckId'];
    const [thisDeck, setThisDeck] = useState({});
    const [deckCards, setDeckCards] = useState([]);
    const [error, setError] = useState(undefined);
    useEffect(() => {
        const abortController = new AbortController();
        readDeck(deckId,
            abortController.signal).then(setThisDeck).catch(setError);
    }, []);

    useEffect(() => {
        const abortController = new AbortController();
        listCards(deckId,
            abortController.signal).then(setDeckCards).catch(setError);
    }, []);
    if (error) {
        return "ERROR " + error; // <ErrorMessage error={error} />;
    }

    const cardComponents = deckCards.map((cardObj) =>
        <DeckCard key={cardObj['id']} cardObj={cardObj}/>);

    const BASE_URL = `/users/${thisDeck['id']}`;
    const EDIT_URL = `${url}/edit`;
    return (
        <article className="col-12 col-md-6 col-xl-3 my-2 align-self-stretch">
            
            <header>
                <NavLink to="/">Home</NavLink> / React Router
            </header>
            <div className="border p-4 h-100 d-flex flex-column">
                <Switch>
                    <Route path={EDIT_URL}>
                        <DeckEdit deckId={deckId}/>
                    </Route>
                </Switch>
            </div>
            <Switch>
                <Route exact={true} path={url}>
                    <h2>Cards</h2>
                    <section className="row">{cardComponents}</section>
                </Route>
            </Switch>
            {/* <h2>Cards</h2>
            <section className="row">{cardComponents}</section> */}
        </article>
    )};

export default DeckView;
