import React, { useState, useEffect } from "react";
import { useHistory, useParams, useRouteMatch, NavLink } from "react-router-dom";
import { readDeck } from "../../utils/api/index";
import StudyCard from "./StudyCard";
const MIN_CARDS = 3;

function NotEnough(numCards, browserHistory, addCardUrl) {
    return (
        <div>
            <h2>Not enough cards</h2>
            <p>
                You need at least {MIN_CARDS} cards to study. There are {numCards} cards in this 
                deck.
            </p>
            <button
                type="button"
                onClick={() => {
                    browserHistory.push(addCardUrl)}}
            >
                Add Cards</button>
        </div>
    );
}

export const Study = () => {
    // console.log("ENTERING STUDY COMPONENT");
    const history = useHistory();
    const { url } = useRouteMatch();
    const params = useParams();
    const deckId = params['deckId'];
    console.log("STUDY DECK ID, ", deckId);
    // const deckUrl = url.substr(0, (url.length - "cards/new".length) - 1)

    const [thisDeck, setThisDeck] = useState({});
    // const [deckName] = "";
    const [cardList, setCardList] = useState([]);
    // let cardList = [];
    const [counter, setCounter] = useState(0);
    const incrementCounter = (x) => setCounter(x+1);
    useEffect(() => {
        setThisDeck({});
        const abortController = new AbortController();
        async function loadCards() {
            try {
                let response = await readDeck(deckId, abortController.signal);
                console.log('STUDY RESPONSE CARDS was: ', response['cards']);
                // let deckFromAPI = response;
                setThisDeck(response);
                // deckName = thisDeck['name'];
                // setCardList(thisDeck['cards']);
                // cardList = response['cards'];
                setCardList(response['cards']);
            }
            catch(error)    {
                return "ERROR " + error; // <ErrorMessage error={error} />;
            }
        }
        loadCards();
        // console.log('POST-SETTING: ', thisDeck);
        return (() => { abortController.abort() });
    }, []);
    
    return(
        <div>
            <header>
                <NavLink to="/">Home</NavLink> / {thisDeck['name']} / Study
            </header>
            {(cardList.length < MIN_CARDS) ? (NotEnough(cardList.length,
                history, `${url}/cards/new`)) : (
                    <StudyCard
                        deckName={thisDeck['name']}
                        cardList={cardList}
                        counterObj={{count: counter, incrementer: incrementCounter}}
                    />)}
      </div>
    );
}

export default Study;
