import React, { useEffect, useState } from "react";
import { useHistory, useParams, useRouteMatch, Link, NavLink } from "react-router-dom";
import { readDeck } from "../../utils/api/index";

export const DeckEdit = ({deckId, deckName}) => {
    const history = useHistory();
    // const params = useParams();
    // console.log("PARAMS IN EDIT, ", params);
    // const deckId = params['deckId'];
    // console.log("DECK ID IN EDIT is ", deckId);

    const [thisDeck, setThisDeck] = useState({});
    const [error, setError] = useState(undefined);

    const [name, setName] = useState("");
    const [desc, setDesc] = useState("");
    const handleNameChange = (event) => setName(event['target']['value']);
    const handleDescChange = (event) => setDesc(event['target']['value']);
    useEffect(() => {
        setThisDeck({}); setName(""); setDesc("");
        const abortController = new AbortController();
        async function loadText() {
            try {
                let response = await readDeck(deckId, abortController.signal);
                // console.log('EDIT RESPONSE was: ', response['description']);
                // let deckFromAPI = response;
                setThisDeck(response);
                setName(thisDeck['name']); setDesc(thisDeck['description']);
            }
            catch(error)    {
                return "ERROR " + error; // <ErrorMessage error={error} />;
            }
        }
        loadText();
        return (() => { abortController.abort() });
    }, []);

    // console.log("EDIT: THIS DECK", thisDeck);
    // console.log("EDIT: NAME", name);
    // console.log("EDIT: DESCRIPTION", desc);
    // setAge(parseInt(event['target']['value']));
    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(name, desc /*.toString()*/);
        // setName(() => ""); setDesc(() => ""); setAge(() => "");
    };
    
    return(
        <div>
            <header>
                <NavLink to="/">Home</NavLink> / {deckName} / Edit Deck
            </header>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">
                    Enter name:
                    <input
                        id="name"
                        type="text"
                        name="name"
                        onChange={handleNameChange}
                        value={name}
                        // defaultValue=""
                    />
                </label>
                <label htmlFor="desc">
                    Description:
                    <textarea
                        id="desc"
                        name="desc"
                        onChange={handleDescChange}
                        value={desc}
                        // defaultValue=""
                        // defaultValue={thisDeck['description']}
                    />
                </label>
                <button type="submit">Submit</button>
                <button onClick={() => history.goBack()}>Cancel</button>
        </form>
      </div>
    );
}

export default DeckEdit;
