import React, { useState } from "react";
import { useParams, useHistory, useRouteMatch, useEffect, NavLink } from "react-router-dom";
import { createCard } from "../../utils/api/index";

export const AddCard = ({deckName}) => {
    const history = useHistory();
    const params = useParams();
    const deckId = params['deckId'];
    const { url } = useRouteMatch();
    const deckUrl = url.substr(0, (url.length - "cards/new".length) - 1);
    
    const [thisDeck, setThisDeck] = useState({});
    const [front, setFront] = useState("");
    const [back, setBack] = useState("");
    const handleFrontChange = (event) => setFront(event['target']['value']);
    const handleBackChange = (event) => setBack(event['target']['value']);

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(front, back /*.toString()*/);
        // setFront(() => ""); setBack(() => ""); setAge(() => "");
        history.push(deckUrl);
    };
    
    return(
        <div>
            <header>
                <NavLink to="/">Home</NavLink> / {deckName} / Add Card
            </header>
            <form onSubmit={handleSubmit}>
                <label htmlFor="front">
                    Front:
                    <textarea
                        id="front"
                        name="front"
                        onChange={handleFrontChange}
                        value={front}
                        placeholder="Front side of card"
                    />
                </label>
                <label htmlFor="back">
                    Description:
                    <textarea
                        id="back"
                        name="back"
                        onChange={handleBackChange}
                        value={back}
                        placeholder="Back side of card"
                    />
                </label>
                <button type="submit">Submit</button>
                <button onClick={() => history.push(deckUrl)}>Done</button>
            </form>
      </div>
    );
}

export default AddCard;
