import React, { useEffect, useState } from "react";
import { Route, useHistory, Link, NavLink } from "react-router-dom";
import { listCards } from "../../utils/api/index";
import truncateText from "../../utils/truncateText";
import DeckView from "./DeckView";
// const allData = require("../../data/db.json");

export const Deck = ({ deckObj /* = { posts: [] } */}) => {
  const history = useHistory();
  const [deckCards, setDeckCards] = useState([]);
  const [error, setError] = useState(undefined);
  useEffect(() => {
    const abortController = new AbortController();
    listCards(deckObj['id'],
      abortController.signal).then(setDeckCards).catch(setError);
  }, []);
  if (error) {
    return "ERROR " + error; // <ErrorMessage error={error} />;
  }
  /*
  const allCards = allData['cards'];
  const deckCards = allCards.filter(x => x['deckId'] === deckObj['id']);
  */
  const BASE_URL = `/decks/${deckObj['id']}`;
  return (
    <article className="col-12 col-md-6 col-xl-3 my-2 align-self-stretch">
      <div className="border p-4 h-100 d-flex flex-column">
        <h2 className="font-weight-lighter flex-fill">
          {deckObj['name']}
        </h2>
        <nav>
          <ul>
            <li>
              {deckCards.length} cards
            </li>
          </ul>
        </nav>
        <p>
          {truncateText(deckObj['description'])}
        </p>
        <nav>
          <ul>
            <li>
              <button
                type="button"
                onClick={() => {history.push(BASE_URL)}}>
                View
              </button>
            </li>
            <li>
              <button>Study</button>
            </li>
            <li>
              <button className="btn btn-danger">Delete</button>
            </li>
          </ul>
        </nav>
      </div>
    </article>
)};

export default Deck;
