import React, { useEffect, useState } from "react";
import { useHistory, useParams, useRouteMatch, Link, NavLink } from "react-router-dom";
import { readDeck, readCard } from "../../utils/api/index";

export const CardEdit = ({deckName}) => {
    const history = useHistory();
    const params = useParams();
    // console.log("PARAMS IN EDIT, ", params);
    const cardId = params['cardId'];
    // console.log("DECK ID IN EDIT is ", cardId);
    const [thisCard, setThisCard] = useState({});
    const [thisDeck, setThisDeck] = useState({});
    const [error, setError] = useState(undefined);

    const [front, setFront] = useState("");
    const [back, setBack] = useState("");
    const handleFrontChange = (event) => setFront(event['target']['value']);
    const handleBackChange = (event) => setBack(event['target']['value']);
    useEffect(() => {
        setThisCard({}); setFront(""); setBack("");
        const abortController = new AbortController();
        async function loadSides() {
            try {
                let response = await readCard(cardId, abortController.signal);
                // console.log('EDIT RESPONSE was: ', response['description']);
                // let deckFromAPI = response;
                setThisCard(response);
                setFront(thisCard['front']); setBack(thisCard['back']);
            }
            catch(error)    {
                return "ERROR " + error; // <ErrorMessage error={error} />;
            }
        }
        loadSides();
        return (() => { abortController.abort() });
    }, []);

    // console.log("EDIT: THIS CARD", thisCard);
    // console.log("EDIT: FRONT", front);
    // console.log("EDIT: BACK", back);
    // setAge(parseInt(event['target']['value']));
    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(front, back /*.toString()*/);
        // setFront(() => ""); setBack(() => ""); setAge(() => "");
    };
    
    return(
        <div>
            <header>
                <NavLink to="/">Home</NavLink> / Deck {deckName} / Edit Card {cardId}
            </header>
            <form onSubmit={handleSubmit}>
                <label htmlFor="front">
                    Front:
                    <textarea
                        id="front"
                        name="front"
                        onChange={handleFrontChange}
                        value={front}
                        // defaultValue=""
                        // defaultValue={thisCard['front']}
                    />
                </label>
                <label htmlFor="back">
                    Description:
                    <textarea
                        id="back"
                        name="back"
                        onChange={handleBackChange}
                        value={back}
                        // defaultValue=""
                        // defaultValue={thisCard['description']}
                    />
                </label>
                <button type="submit">Submit</button>
                <button onClick={() => history.goBack()}>Cancel</button>
        </form>
      </div>
    );
}

export default CardEdit;
