import React, { useState } from "react";

export const DeckEdit = () => {
    const params = useParams();
    const deckId = params['deckId'];
    
    const [thisDeck, setThisDeck] = useState({});
    const [error, setError] = useState(undefined);
    useEffect(() => {
        const abortController = new AbortController();
        readDeck(deckId,
            abortController.signal).then(setThisDeck).catch(setError);
    }, []);

    if (error) {
        return "ERROR " + error; // <ErrorMessage error={error} />;
    }
    const [name, setName] = useState(thisDeck['name']);
    const [desc, setDesc] = useState(thisDeck['description']);
    const handleNameChange = (event) => setName(event['target']['value']);
    const handleDescChange = (event) => setDesc(event['target']['value']);
