        <form onSubmit={handleSubmit}>
            <label htmlFor="name">
                Enter name:
                <input
                    id="name"
                    type="text"
                    name="name"
                    onChange={handleNameChange}
                    value={name}
                    defaultValue={thisDeck['name']}
                />
            </label>
            <label htmlFor="desc">
                Description:
                <textarea
                    id="desc"
                    type="text"
                    name="desc"
                    onChange={handleDescChange}
                    value={desc}
                    defaultValue={thisDeck['description']}
                />
            </label>
            <button type="submit">Submit</button>
      </form>
    );
}

