import React from "react";
import { Link, NavLink } from "react-router-dom";
const allData = require("../../data/db.json");
const MAX_CHARS = 100;

function truncateText(s) {
  return (s.length > MAX_CHARS) ? (s.substr(0, MAX_CHARS) + "...") : (s);
}

// TENTATIVELY DONE
/*
  TODO: Change the link below to go to the user route, using the user's ID.
  /users/:userId
*/
export const Deck = ({ deckObj /* = { posts: [] } */}) => {
  const allCards = allData['cards'];
  const deckCards = allCards.filter(x => x['deckId'] === deckObj['id']);
  const BASE_URL = `/users/${deckObj['id']}`;
  return (
    <article className="col-12 col-md-6 col-xl-3 my-2 align-self-stretch">
      <div className="border p-4 h-100 d-flex flex-column">
        <h2 className="font-weight-lighter flex-fill">
          {deckObj['name']}
        </h2>
        <nav>
          <ul>
            <li>
              <NavLink className="mt-2" to="/">
                {deckCards.length} Cards Â»
              </NavLink>
            </li>
          </ul>
        </nav>
        <p>
          {truncateText(deckObj['description'])}
        </p>
        <nav>
          <ul>
            <li>
              <button>View</button>
            </li>
            <li>
              <button>Study</button>
            </li>
            <li>
              <button>Delete</button>
            </li>
          </ul>
        </nav>
      </div>
    </article>
)};

export default Deck;
